module.exports.perimeterSquare = squareEdge => squareEdge * 4
module.exports.areaSquare = squareEdge => squareEdge * squareEdge
module.exports.volumeSquare = squareEdge => squareEdge * squareEdge * squareEdge

module.exports.perimeterCircle = radius => 2 * Math * PI * radius
module.exports.areaCircle = radius => Math * PI * radius * radius
module.exports.volumeCircle = (radius, height) => height * PI * radius * radius

module.exports.perimeterRectangle = (width, height) => 2 * (width + height)
module.exports.areaRectangle = (width, height) => width * height
module.exports.volumeRectangle = (length, width, height) => length * width * height